 select (select 'cancer' from dual) as entity, (select '30' from dual) as totalTeoricoRecomendado, r.usuarioID as usuario, count(*) as totalResposta, sum(r.votosPositivos) as somaUp, sum(r.votosNegativos) as somaDown from resposta r where r.perguntaID in ( 	 	select p.id from pergunta p  		inner join anotacoes a on p.id = a.id_text 		inner join entidades e on e.id = a.id_entidade 	where  (a.tipo = 'titulo_pergunta' or a.tipo = 'texto_pergunta') 		and lower(e.surface) = lower('cancer') 		and p.id like '1_%' 	 	)  and r.usuarioID in ( 		'1_389','1_69','1_6','1_28','1_86','1_4101','1_3340','1_3632','1_740','1_1306','1_535','1_4579','1_5144','1_5148','1_1757','1_5694','1_115','1_1231','1_307','1_66','1_1320','1_560','1_1441','1_948','1_3053','1_3059','1_107','1_1590','1_3460','1_4177' 	 	) group by r.usuarioID order by 2 desc