 select (select 'genetic code' from dual) as entity, (select '24' from dual) as totalTeoricoRecomendado, r.usuarioID as usuario, count(*) as totalResposta, sum(r.votosPositivos) as somaUp, sum(r.votosNegativos) as somaDown from resposta r where r.perguntaID in ( 	 	select p.id from pergunta p  		inner join anotacoes a on p.id = a.id_text 		inner join entidades e on e.id = a.id_entidade 	where  (a.tipo = 'titulo_pergunta' or a.tipo = 'texto_pergunta') 		and lower(e.surface) = lower('genetic code') 		and p.id like '1_%' 	 	)  and r.usuarioID in ( 		'1_4108','1_3971','1_389','1_560','1_43','1_3632','1_28','1_740','1_2938','1_3615','1_4101','1_3340','1_1306','1_1320','1_430','1_3776','1_1590','1_948','1_535','1_5144','1_6','1_112','1_806','1_4177' 	 	) group by r.usuarioID order by 2 desc