 select (select 'amphibians' from dual) as entity, (select '7' from dual) as totalTeoricoRecomendado, r.usuarioID as usuario, count(*) as totalResposta, sum(r.votosPositivos) as somaUp, sum(r.votosNegativos) as somaDown from resposta r where r.perguntaID in ( 	 	select p.id from pergunta p  		inner join anotacoes a on p.id = a.id_text 		inner join entidades e on e.id = a.id_entidade 	where  (a.tipo = 'titulo_pergunta' or a.tipo = 'texto_pergunta') 		and lower(e.surface) = lower('amphibians') 		and p.id like '1_%' 	 	)  and r.usuarioID in ( 		'1_3615','1_86','1_107','1_112','1_1231','1_3632','1_3971' 	 	) group by r.usuarioID order by 2 desc