 select (select 'blood' from dual) as entity, (select '12' from dual) as totalTeoricoRecomendado, r.usuarioID as usuario, count(*) as totalResposta, sum(r.votosPositivos) as somaUp, sum(r.votosNegativos) as somaDown from resposta r where r.perguntaID in ( 	 	select p.id from pergunta p  		inner join anotacoes a on p.id = a.id_text 		inner join entidades e on e.id = a.id_entidade 	where  (a.tipo = 'titulo_pergunta' or a.tipo = 'texto_pergunta') 		and lower(e.surface) = lower('blood') 		and p.id like '1_%' 	 	)  and r.usuarioID in ( 		'1_3632','1_86','1_948','1_806','1_1320','1_3615','1_5144','1_5754','1_3340','1_740','1_107','1_560' 	 	) group by r.usuarioID order by 2 desc