 select (select 'vaccine' from dual) as entity, (select '15' from dual) as totalTeoricoRecomendado, r.usuarioID as usuario, count(*) as totalResposta, sum(r.votosPositivos) as somaUp, sum(r.votosNegativos) as somaDown from resposta r where r.perguntaID in ( 	 	select p.id from pergunta p  		inner join anotacoes a on p.id = a.id_text 		inner join entidades e on e.id = a.id_entidade 	where  (a.tipo = 'titulo_pergunta' or a.tipo = 'texto_pergunta') 		and lower(e.surface) = lower('vaccine') 		and p.id like '1_%' 	 	)  and r.usuarioID in ( 		'1_3615','1_86','1_4168','1_67','1_3340','1_5144','1_1320','1_1757','1_5148','1_5694','1_3632','1_1231','1_806','1_948','1_3971' 	 	) group by r.usuarioID order by 2 desc